import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { Link, useHistory, useParams } from 'react-router-dom';

const ViewUser = () => {
    let history = useHistory();
    const {id} = useParams();
    const [user, setUser] = useState({
        name: '',
        username: '',
        email: '',
        phone: '',
        website: ''
    })

    const { name, username, email, phone, website } = user;;



    useEffect(()=>{
        Loaduser();
    },[]);
    
    const Loaduser = async () => {
        const result = await axios.get(`http://localhost:3003/Users/${id}`);
        setUser(result.data)
    }
    return (
        <div className="container shadow py-5">
            <h1 className="text-center my-5">View User</h1>
            <Link ></Link>
            <form>
                <div className="form-group my-3 ">
                    <input type="text" name="name" className="form-control" value={name} placeholder="Enter Name" />
                </div>
                <div className="form-group my-3">
                    <input type="text" name="username" className="form-control" value={username} placeholder="Enter Username" />
                </div>
                <div className="form-group my-3">
                    <input type="email" name="email" className="form-control" value={email} placeholder="Enter Email" />
                </div>
                <div className="form-group my-3">
                    <input type="tel" name="phone" className="form-control" value={phone} placeholder="Enter Number" />
                </div>
                <div className="form-group my-3">
                    <input type="text" name="website" className="form-control" value={website} placeholder="Enter Website" />
                </div>
                {/* <button type="submit" className="btn btn-warning btn-lg w-100 btn-block">Update</button> */}
            </form>
        </div>
    )
}

export default ViewUser
